package carSystem.com.vo;import carSystem.com.bean.Customer;import carSystem.com.bean.Report;import carSystem.com.bean.User;import java.util.Arrays;import java.util.List;public class ReportTableVO {    private Report report;    private String nickName;    private String service;    public static String toService(String serviceList) {        String s = "";        List<String> stringList = Arrays.asList(serviceList.split(","));        for (String a : stringList) {            switch (a) {                case "1" : s = s + "【反诈骗策略引擎】";                break;                case "2" : s = s + "【银行卡四要素】";                break;                case "3" : s = s + "【手机三要素】";                break;                case "4" : s = s + "【手机在网时长】";                break;                case "5" : s = s + "【手机在网状态】";                break;                case "6" : s = s + "【综合查询】";            }        }        return s;    }    public Report getReport() {        return report;    }    public void setReport(Report report) {        this.report = report;    }    public String getNickName() {        return nickName;    }    public void setNickName(String nickName) {        this.nickName = nickName;    }    public String getService() {        return service;    }    public void setService(String service) {        this.service = service;    }}